import sys
import json
import os
from thefuzz import fuzz
import re
import pinyin

query = sys.argv[1]
fuzz_value = int(os.environ["fuzz_value"])

def contains_chinese(s):
    return re.findall(r'[\u4e00-\u9fff]+', s)

def one_word_matched(query, target):
    query = query.lower()
    target = target.lower()

    query_words = query.split()
    if query_words == 1:
        return any(fuzz.ratio(query, w) > fuzz_value for w in target.split())
    return any(any(fuzz.ratio(qw, w) > fuzz_value for w in target.split()) for qw in query_words)

def pinyin_matched(query, target):
    if not contains_chinese(target):
        return False
    # explicitly replace some known errors in pinyin transcription
    with open("replace_d.json", "r", encoding="utf-8") as f:
        replace_d = json.load(f)

    for k, v in replace_d.items():
        target = target.replace(k, v)

    target_py = pinyin.get(target, format="strip", delimiter="")
    return len(query) > 1 and query in target_py

termbase_dir = os.environ["termbase_dir"]
termbase_names = [p for p in os.listdir(termbase_dir) if p.endswith(".json")]
termbase_paths = [os.path.join(termbase_dir, fn) for fn in termbase_names]
paired = list(zip(termbase_names, termbase_paths))

formatted_lst = []

for p in paired:
    with open(p[1], "r", encoding="utf-8") as f:
        tb = json.load(f)

    for k, v in tb.items():
        if contains_chinese(query):
            if query in k:
                formatted_lst.append({
                        "title": k,
                        "subtitle" : " | ".join(v),
                        "arg" : f"{p[1]}||{k}||{' | '.join(v)}",
                        "mods" : {
                            "cmd" : {
                                "valid" : True,
                                "arg" : f"{p[1]}||{k}||{' | '.join(v)}",
                                "subtitle" : p[0]
                            }
                        }
                    })
            elif any(query in tt for tt in v):
                for tt in v:
                    if query in tt:
                        formatted_lst.append({
                                "title": f"{tt} üîÅ",
                                "subtitle": k,
                                "arg": f"rev||{k}||{tt}",
                                "mods": {
                                    "cmd" : {
                                        "valid" : True,
                                        "arg" : f"rev||{k}||{tt}",
                                        "subtitle" : p[0]
                                    }
                                }
                            })
        else:
            if one_word_matched(query, k) or pinyin_matched(query, k):
                formatted_lst.append({
                        "title": k,
                        "subtitle" : " | ".join(v),
                        "arg" : f"{p[1]}||{k}||{' | '.join(v)}",
                        "mods": {
                            "cmd" : {
                                "valid" : True,
                                "arg" : f"{p[1]}||{k}||{' | '.join(v)}",
                                "subtitle" : p[0]
                            }
                        }
                    })
            elif any(one_word_matched(query, tt) or pinyin_matched(query, tt) for tt in v):
                for tt in v:
                    if one_word_matched(query, tt) or pinyin_matched(query, tt):
                        formatted_lst.append({
                                "title": f"{tt} üîÅ",
                                "subtitle": k,
                                "arg": f"rev||{k}||{tt}",
                                "mods": {
                                    "cmd" : {
                                        "valid" : True,
                                        "arg" : f"rev||{k}||{tt}",
                                        "subtitle" : p[0]
                                    }
                                }
                            })

formatted_d = {"items" : list(reversed(formatted_lst))}

data = json.dumps(formatted_d)

sys.stdout.write(data)

import sys
import json
import os

st = os.environ["st"]
tb_to_search = os.environ["tb_to_search"]
tb_name = os.path.basename(tb_to_search)

formatted_lst = []

with open(tb_to_search, "r", encoding="utf-8") as f:
    tb = json.load(f)

results = tb[st]


for r in results:
    formatted_lst.append({
            "title" : f"{st}Ôºà{r}Ôºâ",
            "subtitle" : "Hold CMD to copy only TT.",
            "arg" : f"{st}Ôºà{r}Ôºâ",
            "mods": {
                "cmd" : {
                    "valid" : True,
                    "arg" : st,
                    "subtitle" : "Only TT will be copied."
                }
            }
        })

formatted_d = {"items" : formatted_lst}

data = json.dumps(formatted_d)

sys.stdout.write(data)

import sys
import json
import os
from thefuzz import fuzz
import re
import pinyin

query = sys.argv[1]
fuzz_value = int(os.environ["fuzz_value"])

def contains_chinese(s):
    return re.findall(r'[\u4e00-\u9fff]+', s)

def one_word_matched(query, target):
    query = query.lower()
    target = target.lower()

    query_words = query.split()
    if query_words == 1:
        return any(fuzz.ratio(query, w) > fuzz_value for w in target.split())
    return any(any(fuzz.ratio(qw, w) > fuzz_value for w in target.split()) for qw in query_words)

def pinyin_matched(query, target):
    if not contains_chinese(target):
        return False
    # explicitly replace some known errors in pinyin transcription
    with open("replace_d.json", "r", encoding="utf-8") as f:
        replace_d = json.load(f)

    for k, v in replace_d.items():
        target = target.replace(k, v)

    target_py = pinyin.get(target, format="strip", delimiter="")
    return len(query) > 1 and query in target_py

termbase_dir = os.environ["termbase_dir"]
termbase_names = [p for p in os.listdir(termbase_dir) if p.endswith(".json")]
termbase_paths = [os.path.join(termbase_dir, fn) for fn in termbase_names]
paired = list(zip(termbase_names, termbase_paths))
formatted_lst = []

exact_match_found = False

for p in paired:
    with open(p[1], "r", encoding="utf-8") as f:
        tb = json.load(f)

    for k, v in tb.items():
        if query == k:
            exact_match_found = True
        if contains_chinese(query):
            if query in k:
                formatted_lst.append({
                        "title": k,
                        "subtitle" : " | ".join(v),
                        "arg" : f"{p[1]}||{k}|{' | '.join(v)}",
                        "mods": {
                            "cmd" : {
                                "valid": True,
                                "arg": f"{p[1]}||{k}|{' | '.join(v)}",
                                "subtitle": p[0]
                            }
                        }
                    })
            elif any(query in tt for tt in v):
                for tt in v:
                    if query in tt:
                        formatted_lst.append({
                                "title": k,
                                "subtitle": f'‚úÖ {" | ".join(v)}',
                                "arg": f"{p[1]}||{k}||{' | '.join(v)}",
                                "mods": {
                                    "cmd" : {
                                        "valid": True,
                                        "arg": f"{p[1]}||{k}||{' | '.join(v)}",
                                        "subtitle": p[0]
                                    }
                                }
                            })
        else:
            if one_word_matched(query, k) or pinyin_matched(query, k):
                formatted_lst.append({
                        "title": k,
                        "subtitle" : " | ".join(v),
                        "arg" : f"{p[1]}||{k}||{' | '.join(v)}",
                        "mods": {
                            "cmd" : {
                                "valid": True,
                                "arg": f"{p[1]}||{k}||{' | '.join(v)}",
                                "subtitle": p[0]
                            }
                        }
                    })
            elif any(one_word_matched(query, tt) or pinyin_matched(query, tt) for tt in v):
                for tt in v:
                    if one_word_matched(query, tt) or pinyin_matched(query, tt):
                        to_add = {
                                "title": k,
                                "subtitle": f'‚úÖ {" | ".join(v)}',
                                "arg": f"{p[1]}||{k}||{' | '.join(v)}",
                                "mods": {
                                    "cmd" : {
                                        "valid": True,
                                        "arg": f"{p[1]}||{k}||{' | '.join(v)}",
                                        "subtitle": p[0]
                                    }
                                }
                            }
                        if to_add not in formatted_lst:
                            formatted_lst.append(to_add)

if not exact_match_found:
    formatted_lst.append({
            "title" : query,
            "subtitle" : "This will be added as a new entry.",
            "arg" : f"new||{query}",
            "icon" : {"path" : "addnewentry.png"}
        })

formatted_d = {"items" : list(reversed(formatted_lst))}

data = json.dumps(formatted_d)

sys.stdout.write(data)

